def find_sum(lst):
    left = 0
    right = len(lst) - 1
    while left < right:
        s = lst[left] + lst[right]
        if s == 100:
            return [lst[left], lst[right]]
        elif s < 100:
            left += 1
        else:
            right -= 1
    return None

